document.addEventListener("DOMContentLoaded",function(){const t=document.querySelectorAll(".floating-toc .toc-link"),n=document.querySelectorAll("h1, h2, h3, h4, h5, h6");function e(){let e=window.scrollY;n.forEach(n=>{n.offsetTop<=e+100&&t.forEach(e=>{e.getAttribute("href")==="#"+n.id?e.classList.add("active"):e.classList.remove("active")})})}window.addEventListener("scroll",e),e()})