document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".floating-toc .toc-link"),t=document.querySelectorAll("h1, h2, h3, h4, h5, h6");console.log("TOC Links:",e.length),console.log("Sections:",t.length);function n(){let n=window.scrollY;console.log("Scroll position:",n),t.forEach(t=>{t.offsetTop<=n+100&&(console.log("Current section:",t.id),e.forEach(e=>{e.getAttribute("href")==="#"+t.id?(console.log("Activating link:",e.getAttribute("href")),e.classList.add("active")):e.classList.remove("active")}))})}window.addEventListener("scroll",n),n()})